const Jimp = require('jimp');

const funcion  = async () =>{
    // Obtener la imagen dibujada en base64 desde el cuerpo de la solicitud
    const base64Drawing = "data:image/png;base64,";

    // Decodificar la imagen dibujada de base64 a buffer
    const bufferDrawing = Buffer.from(base64Drawing, 'base64');

    // Cargar la imagen definida (puedes reemplazar 'defined-image.jpg' por la ruta de tu imagen definida)
    const definedImage = await Jimp.read('defined-image.jpg');

    // Cargar la imagen dibujada desde el buffer
    const handDrawing = await Jimp.read(bufferDrawing);

    // Ajustar las imágenes al mismo tamaño
    handDrawing.resize(definedImage.getWidth(), definedImage.getHeight());

    // Comparar las imágenes pixel a pixel
    const diff = Jimp.diff(handDrawing, definedImage);

    // Calcular el porcentaje de diferencia
    const similarity = 1 - (diff.percent / 100);

    // Establecer un umbral de similitud (puedes ajustar este valor según tus necesidades)
    const similarityThreshold = 0.8;

    // Determinar si las imágenes son similares o no
    const isSimilar = similarity >= similarityThreshold;

    // Devolver el resultado al cliente
    console.log({ similarity, isSimilar })
}

funcion()
